import React, { useState, FormEvent } from 'react';

import PageHeader from '../../components/PageHeader';
import Input from '../../components/Input';

import './styles.css'
import warningIcon from '../../assets/images/icons/warning.svg'
import Textarea from '../../components/TextArea';
import Select from '../../components/Select';
import api from '../../services/api';

function TeacherForm() {
    const [name, setName] = useState('');
    const [avatar, setAvatar] = useState('');
    const [whatsapp, setWhatsapp] = useState('');
    const [bio, setBio] = useState('');

    const [subject, setSubject] = useState('');
    const [cost, setCost] = useState('');

    const [scheduleItems, setScheduleItems] = useState([
        { week_day: 0, from: "", to: "" }
    ])


    function addNewScheduleItem() {
        setScheduleItems([
            ...scheduleItems,
            { week_day: 0, from: "", to: "" }
        ]);

    }

    function setScheduleItemValue(position: Number, field: string, value: string) {
        const updatedScheduleItem = scheduleItems.map((scheduleItem, index) => {
            if (index === position) {
                return { ...scheduleItem, [field]: value }
            }
            return scheduleItem;
        });
        setScheduleItems(updatedScheduleItem);
    }

    function handleCreateClass(e: FormEvent) {
        api.post('classes',{
            name,
            avatar,
            whatsapp,
            bio,
            subject,
            cost: Number,
            schedule: scheduleItems

        }).then(()=>{
            alert('Cadastro realizado com sucesso!')
        }).catch(()=> {
            alert('Erro no cadastro.')
        })
    }

    return (
        <div id="page-teacher-form" className="container">
            <PageHeader
                title="Que incrível que você quer dar aulas."
                description="O primeiro passo é preencher esse formulário de inscrição."
            />

            <main>
                <form onSubmit={handleCreateClass}>
                    <fieldset>
                        <legend>Seus dados</legend>
                        <Input
                            name="name"
                            label="Nome Completo"
                            value={name}
                            onChange={(e) => { setName(e.target.value) }}></Input>
                        <Input
                            name="avatar"
                            label="Avatar"
                            value={avatar}
                            onChange={(e) => { setAvatar(e.target.value) }}></Input>
                        <Input
                            name="whatsapp"
                            label="Whatsapp"
                            value={whatsapp}
                            onChange={(e) => { setWhatsapp(e.target.value) }}></Input>
                        <Textarea
                            name="bio"
                            label="Biografia"
                            value={bio}
                            onChange={(e) => { setBio(e.target.value) }}></Textarea>
                    </fieldset>
                    <fieldset>
                        <legend>Sobre a aula</legend>
                        <Select
                            name="subject"
                            label="Matéria"
                            value={subject}
                            onChange={(e) => { setSubject(e.target.value) }}
                            options={[
                                { value: "Artes", label: "Artes" },
                                { value: "Biologia", label: "Biologia" },
                                { value: "Geografia", label: "Geografia" },
                                { value: "Matematica", label: "Matematica" },
                                { value: "Ingles", label: "Ingles" },
                                { value: "Historia", label: "Historia" },
                                { value: "Fisica", label: "Fisica" },
                                { value: "Portugues", label: "Portugues" },
                                { value: "Quimica", label: "Quimica" },
                            ]}
                        />
                        <Input
                            name="cost"
                            label="Custo da sua hora por aula"
                            value={cost}
                            onChange={(e) => { setCost(e.target.value) }}></Input>
                    </fieldset>
                    <fieldset>
                        <legend>Horários disponíveis
                    <button type="button" onClick={addNewScheduleItem}>+ Novo Horário</button>
                        </legend>

                        {scheduleItems.map((scheduleItem, index) => {
                            return (
                                <div key={scheduleItem.week_day} className="schedule-item">
                                    <Select
                                        name="week_day"
                                        label="Dia da semana"
                                        value={scheduleItem.week_day}
                                        onChange={e => setScheduleItemValue(index, 'week_day', e.target.value)}
                                        options={[
                                            { value: "0", label: "Domingo" },
                                            { value: "1", label: "Segunda-Feira" },
                                            { value: "2", label: "Terça-Feira" },
                                            { value: "3", label: "Quarta-Feira" },
                                            { value: "4", label: "Quinta-Feira" },
                                            { value: "5", label: "Sexta-Feira" },
                                            { value: "6", label: "Sabádo" },
                                        ]}
                                    />
                                    <Input
                                        type="time"
                                        name="from"
                                        label="Das"
                                        value={scheduleItem.from}
                                        onChange={e => setScheduleItemValue(index, 'from', e.target.value)}></Input>
                                    <Input
                                        type="time"
                                        name="to"
                                        label="Até"
                                        value={scheduleItem.to}
                                        onChange={e => setScheduleItemValue(index, 'to', e.target.value)}></Input>
                                </div>

                            )
                        })}


                    </fieldset>
                    <footer>
                        <p>
                            <img src={warningIcon} alt="Aviso importante" />
                        importante! <br />
                        Preencha todos os dados
                    </p>
                        <button type="submit">Salvar </button>
                    </footer>
                </form>
            </main>
        </div>
    )
}

export default TeacherForm;
